import React, {Component} from 'react';

export default class Login extends Component {
    constructor(props){
        super(props);
        
        this.state = {
            mode: "login"
        }

    }

    toggleMode(){
        if(this.state.mode==="login"){
            this.setState({
                mode: "sign-up"
            })
        } else {
            this.setState({
                mode: "login"
            })
        }
    }

    loginForm(){
        <div>
            <h1>LOGIN</h1>
            <button action={this.toggleMode()}></button>
        </div>
    }

    signupForm(){
        return(
            <div>
                <h1>SIGNUP</h1>
                <button action={this.toggleMode()}></button>
            </div>
        )
    }

    render(){
        {this.state.mode==="login" ? this.loginForm() : this.signupForm()}
    }


}
