import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link} from "react-router-dom";
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import {hasRole, isAuth} from "./auth.js"

//Main Components
import Home from "./components/home.component.js";
import SubmitPage from "./components/submitPage.component.js";
import SubmissionList from "./components/submissionList.component.js";
import About from "./components/about.component.js";


class App extends Component {
  constructor(props){
    super(props);
  }

  render() {
    return(

      <Router>
        <div>
          <nav className="navbar" id="sidebar">
            <Link to="/">
              Home
            </Link>
            <Link to="/submit">
              Submit
            </Link>
            <Link to="/submissionList">
              Submissions
            </Link>
            <Link to="/about">
              About
            </Link>
          </nav>
          <Route path="/" exact component={Home}/>
          <Route path="/submit" component={SubmitPage}/>
          {hasRole(this.props.user, ['admin']) && <Route path="/submissionList" component={SubmissionList}/>}
          <Route path="/about" component={About}/>
        </div>
        <div>
            {hasRole(this.props.user, ['user']) && <p>Is User</p>}
            {hasRole(this.props.user, ['admin']) && <p>Is Admin</p>}
        </div>
      </Router>

    );
  }
}

