import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link} from "react-router-dom";
import axios from 'axios';
const BACK_PORT = 4000;


export default class AuthForm extends Component {
    constructor(props){
        super(props);
        
         this.state = {
            username: '',
            password: '',
            type: this.props.type
        }

        this.toggleMode = this.toggleMode.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);



    }

    toggleMode(){
        if(this.state.type==='login'){
            this.setState({
                type: 'signup'
            })
            this.props.history.push('/signup');
        } else {
            this.setState({
                type: 'login'
            })
            this.props.history.push('/login');
        }
    }

    onChangePassword(e){
        this.setState({
            password: e.target.value
        });
    }

    onChangeUsername(e){
        this.setState({
            username: e.target.value
        });
    }

    handleSubmit(ev){
        //api will handle hashing
        ev.preventDefault();
        //VERSION FOR DB
        const userSubmission= {
            username: this.state.username,
            password: this.state.password,
        }

        if(this.state.type==='signup'){
            axios.post(`http://localhost:${BACK_PORT}/molten/users/sign-up`,userSubmission)
            .then((res)=>{
                //const parsedRes = JSON.parse(res);
                console.log(JSON.stringify(res));
                //console.log(`Signed up ${res.response.user_name}`);
                this.props.loginUser(userSubmission);
                this.props.history.push('/'); //need to be async??
            });
        } else {
            console.log("about to login user");
            this.props.loginUser(userSubmission);
            this.props.history.push('/'); //need to be async??
        }

        //VERSION FOR TEST
        // const newSignup= {
        //     user_name: this.state.username,
        //     //user_pass: this.state.password,
        //     user_roles: ['user']
        // }

        //TODO: Post to /users/sign-up

    }

    render(){
        return(
            <Router>
            <div>
                <form onSubmit = {this.handleSubmit}>
                    <div className = "form-group">
                        <label htmlFor="username" className="mb-1 text-muted">
                                Username
                        </label>
                        <input  type="text" 
                                name="username" 
                                id="username" 
                                onChange={this.onChangeUsername}
                                value={this.state.username} 
                                className="form-control rounded-0" 
                                required minLength="5" />
                        
                    </div>
                    <div className = "form-group">
                        <label htmlFor="password" className="mb-1 text-muted">
                                Password
                        </label>
                        <input  type="text" 
                                name="password" 
                                id="password" 
                                onChange={this.onChangePassword}
                                value={this.state.password} 
                                className="form-control rounded-0" 
                                required minLength="5" />
                    </div>

                    <div className="form-group">
                        <input type="submit" value={this.state.type === 'signup' ? "Sign Up" : "Login"} className="btn btn-primary"></input>
                    </div>
					
                    <button onClick={this.toggleMode} >{this.state.type === 'signup' ? "(Login Instead)" : "(Sign Up Instead)"}</button>

							
                        
                </form>
            </div>
            </Router>
        )
    }


}
