import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link} from "react-router-dom";
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import {hasRole, isAuth} from "./auth.js"
import {UserProvider, UserConsumer} from "./userContext.js"

//Main Components
import Home from "./components/home.component.js";
import SubmitPage from "./components/submitPage.component.js";
import SubmissionList from "./components/submissionList.component.js";
import About from "./components/about.component.js";
import Login from "./components/login.component.js";



class App extends Component {
  constructor(props){
    super(props);

    //Currently logged in user
    this.state = {
      user:{}
    }

  }

  render() {
    return(
      <Router>
        <UserProvider value={this.state.user}>
        <div>
          <nav className="navbar" id="sidebar">
            <h3>{this.state.user.user_name}</h3>
            <Link to="/">
              Home
            </Link>
            <Link to="/submit">
              Submit
            </Link>
            {hasRole(this.props.user, ['admin']) && <Link to="/submissionList">Submissions</Link>}
            <Link to="/about">
              About
            </Link>
            <Link to="/login">
              Login
            </Link>
          </nav>
          <Route path="/" exact component={Home}/>
          <Route path="/submit" component={SubmitPage}/>
          <Route path="/submissionList" component={SubmissionList}/>
          <Route path="/about" component={About}/>
          <Route path="/login" component={Login}/>

        </div>
        <div>
            {hasRole(this.props.user, ['user']) && <p>Is User</p>}
            {hasRole(this.props.user, ['admin']) && <p>Is Admin</p>}
        </div>
        </UserProvider>
      </Router>


    );
  }
}

