import React, {Component} from 'react';
import Submission from './submission.component.js'
import axios from 'axios';
const BACK_PORT = 4000;

export default class SubmissionList extends Component {
    constructor(props){
        super(props);

        this.state = {submissions: [],
                      images: ["daffycoloado.JPG"]    
        };
    }

    componentDidMount(){
        axios.get(`http://localhost:${BACK_PORT}/molten/submissions`)
            .then(res => {
                this.setState({submissions: res.data,
                });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    submissionList(){
        return this.state.submissions.map(function(currentSub, i){
            return <Submission sub={currentSub} key={i}/>
        })
    }

    //refactor to a new js file if this is working
    fetchImages() {
            const imageName = 'daffycolorado.JPG'
            const url = `http://localhost:4000/fetchImage/${imageName}`
            axios.get(url, {responseType: 'blob'})
            .then(res => {
                console.log(`ImageData: ${res.data} `)
                //var file = new File( res.data, "image", { type: "image/jpeg" } );
                //var imageUrl = URL.createObjectURL(file);
                    return <img src={res.data} alt="trial" />                      
            })
        }

    render(){
        return(
            <div>
                <h3>Active Submissions</h3>
                <table className="table table-striped" style={{marginTop: 20}} >
                    <tbody>
                        {this.submissionList()}
                    </tbody>
                </table>
                <div>
                    {this.fetchImages()}
                </div>
            </div>
        );
    }
}
