    //                 </div>
    //             <br />
    //             <div>
    //                 <button>Upload</button>
    //             </div>
    //             <div>
    //                 {this.state.didUpload ? this.state.fileURL : ''}
    //             </div>
    //             </form>
    //         </div>
    //     );
    // }
}
