import React, {Component} from 'react';

export default class Login extends Component {
    constructor(props){
        super(props);
        
        this.state = {
            login: true
        }

        this.toggleMode = this.toggleMode.bind(this);

    }

    toggleMode(){
        if(this.state.login===true){
            this.setState({
                login: false
            })
        } else {
            this.setState({
                login: true
            })
        }
    }

    loginForm(){
        return(
        <div>
            <h1>LOGIN</h1>
            <button onClick={this.toggleMode}>Sign Up</button>
        </div>
        )
    }

    signupForm(){
        return(
            <div>
                <h1>SIGNUP</h1>
                <button onClick={this.toggleMode}>Login</button>
            </div>
        )
    }

    render(){
        return(
            <div>
            {this.state.mode==="login" ? this.loginForm() : this.signupForm()}
            </div>
        )
    }


}
